import React, { useState } from 'react'
import TextField from '@mui/material/TextField';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Topbar from '../../components/Topbar';
import './Employee.css'
function Employee() {
  const [name,setName] = useState('');
  const [number,setNumber] = useState('');
  const [age,setAge] = useState('');
  const [allData,setAllData]  = useState([]);
  const submitForm = () => {
    console.log(name,number,age,"pppp");
    let existingData = [... allData];
    existingData.push({name : name,age: age,number :number});
    setAllData(existingData);
  }
  return (
     <div>
          <Topbar />
          <Grid container spacing={3} style={{padding: '3rem'}}>
            <Grid item  md={4}></Grid>
            <Grid item  md={4}>
              <Card>
                <CardContent>
                  <div className="input_margin_btm">
                    <TextField  fullWidth id="outlined-basic" label="Employee Name" variant="outlined" onChange={e => setName(e.target.value)}  />
                  </div>
                  <div className="input_margin_btm">
                    <TextField   fullWidth id="outlined-basic" label="Employee Number" variant="outlined" onChange={e => setNumber(e.target.value)}  />
                  </div>
                  <div className="input_margin_btm">
                    <TextField  fullWidth id="outlined-basic" label="Age" variant="outlined" onChange={e => setAge(e.target.value)} />
                  </div>
                </CardContent>
                <CardActions>
                  <Button fullWidth variant="contained" size="small" onClick={submitForm}>Add Details</Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item  md={4}></Grid>
          </Grid>

          <Grid container spacing={3} style={{padding: '3rem'}}>
            <Grid item  md={3}></Grid>
            <Grid item  md={6}>
                <Card>
                  <CardContent>
                      {
                        allData.map(data => (
                          <>
                            <p>{data.name}</p>
                            <p>{data.age}</p>
                          </>
                        ))
                      }
                  </CardContent>
                </Card>
            </Grid>
            <Grid item  md={3}></Grid>
          </Grid>
     </div>
  )
}

export default Employee
